    <td width="78%" align="left" valign="top" bgcolor="#E8E8E8"><table width="100%"  border="0" cellspacing="5" cellpadding="0">
      <tr>
        <td align="left" valign="top"><h1>Giovanni de' Medici </h1>
          <p>scritta da Gian Girolamo Rossi </p></td>
      </tr>
    </table>    
    </td>
    <td width="2%">&nbsp;</td>
    <td width="20%" align="center" valign="middle"><a href="/historia/index.php" class="Stile1">home</a></td>
